$kyc_events = SELECT * FROM $source;
// Branch for S3 archival of original event
$raw_for_s3 = SELECT * FROM $kyc_events;
$raw_for_s3 | INTO s3_kyc_audit_archive_bucket OPTIONS (format="raw");

// Main branch for processing
$processed_kyc = SELECT * FROM $kyc_events;

...
| fields remove pii.email, pii.phone_number, documents.document_id 
// Retain pii.email_hash, pii.phone_number_masked, documents.document_id_hash

$processed_transactions = SELECT * FROM $raw_transactions
| eval cvv = null(), track1_data = null(), track2_data = null(), pin_block = null() 
// Or use: | fields remove cvv, track1_data, track2_data, pin_block
// Ensure all potential SAD fields are covered based on your specific data sources.

...
| eval pan_masked = if(isnotnull(pan), replace(pan, "^(\\d{6}).*(\\d{4})$", "$1******$2"), null())
// Create a new field for the masked PAN.
// Decide whether to keep original 'pan' field or remove it based on downstream needs and risk assessment.
// For logs sent to general Splunk, it's safer to only have pan_masked.
| eval pan = pan_masked // Overwrite original pan with masked version for this stream
| fields remove pan_masked // Optional: clean up temporary field if pan is overwritten

...
// Route to central Splunk for Req 10 monitoring
| INTO splunk_pci_monitoring_index;

// Optionally, if a separate archive of these sanitized logs is needed:
// $archive_stream = SELECT * FROM $processed_transactions; // Assuming $processed_transactions is the final state
// $archive_stream | INTO s3_pci_sanitized_archive_bucket OPTIONS (format="json");

$events = SELECT * FROM $raw_logs;
// Branch for highly restricted S3 archival of original event (if legally required)
$raw_for_secure_archive = SELECT * FROM $events;
$raw_for_secure_archive | WHERE /* specific conditions for archival */ | INTO s3_gdpr_restricted_raw_archive OPTIONS (format="raw", region="eu-west-1");

// Main branch for processing and pseudonymization
$processed_logs = SELECT * FROM $events;

$processed_logs
// Anonymize IP address (e.g., mask last octet or hash)
| eval client_ip = replace(client_ip, "\\.\\d+$", ".0") 
// Pseudonymize user ID (e.g., using a salted hash if correlation is ever needed under strict controls)
| eval user_id = if(isnotnull(user_id), sha256(concat(user_id, "your_secret_salt")), null())
// Redact or hash PII from JSON request body
| eval request_body = if(isnotnull(request_body), from_json(request_body), null()) // Parse JSON
| eval request_body.email = if(isnotnull(request_body.email), "REDACTED_EMAIL", null())
| eval request_body.full_name = if(isnotnull(request_body.full_name), "REDACTED_NAME", null())
// Re-serialize request_body if needed, or extract necessary non-PII fields
| eval request_body_processed_json = if(isnotnull(request_body), to_json(request_body), null())
// Sanitize URL if it contains PII
| eval url = replace(url, "/user/(\\S+)/details", "/user/USER_ID_REDACTED/details")

...
| fields remove session_id, user_agent_full_string // Example: if only browser family is kept from user_agent
// Keep only fields like: _time, client_ip (anonymized), user_id (pseudonymized), 
// http_method, url (sanitized), status_code, request_body_processed_json (sanitized), etc.

...
| where NOT (log_level == "DEBUG" AND system_criticality == "Tier3") 
| where NOT (message CONTAINS "routine health check status: OK" AND system_criticality != "Tier0")
// Keep all logs from Tier0/Tier1 systems, or logs with ERROR/CRITICAL severity regardless of tier.
| where system_criticality IN ("Tier0", "Tier1") OR severity_level IN ("ERROR", "CRITICAL", "FATAL") OR isnotnull(change_id);

...
// Route critical security/operational alerts
| IF severity_level IN ("CRITICAL", "FATAL") AND system_criticality IN ("Tier0", "Tier1") THEN
    INTO splunk_apac_critical_alerts_index;
// Route change-related logs
| ELSE IF isnotnull(change_id) THEN
    INTO splunk_apac_change_audit_index;
// Route other important operational logs
| ELSE 
    INTO splunk_apac_operational_monitoring_index;
// Optionally, send a copy of all processed data to a regional S3 archive for RMiT record keeping
// | CLONE dest_count=2 // if not already handled by multiple INTO
// | WHERE _clone_id == 1 
// | INTO s3_apac_rmit_processed_logs_archive OPTIONS(format="json", region=data_center_location); // Dynamic region based on enrichment

...
| where is_significant_incident == "true" OR (event_type == "SERVICE_PROVIDER_ALERT" AND isnotnull(service_provider_involved)) OR (match(message, /APRA_NOTIFY_KEYWORD/))
// This ensures only events flagged as significant or related to specific CPS 230 concerns are passed on.
// Routine "OK" status from non-COs would be dropped if not meeting these conditions.

...
// Route events potentially requiring APRA notification
| IF is_significant_incident == "true" AND (tolerance_breach_imminent == "true" OR /* other APRA notification criteria */ ) THEN
    INTO splunk_anz_apra_notify_candidates_index;
// Route other significant operational risk events
| ELSE IF is_significant_incident == "true" THEN
    INTO splunk_anz_cps230_significant_events_index;
// Route material service provider related events
| ELSE IF isnotnull(service_provider_involved) THEN
    INTO splunk_anz_service_provider_monitoring_index;
// Optionally, archive a copy of these processed, significant events
// | CLONE dest_count=2 
// | WHERE _clone_id == 1
// | INTO s3_anz_cps230_processed_archive OPTIONS(format="json");
